import React, { useEffect, useState } from "react";

function Tick() {
  const [time, setTime] = useState(new Date().toLocaleTimeString());
  useEffect(() => {
    const timer = setTimeout(() => {
      setTime(new Date().toLocaleTimeString());
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  const element = (
    <div>
      <h1>Hello, world!</h1>
      <h2>It is {time}.</h2>
    </div>
  );

  return element;
}

export default Tick;
