import React, { useEffect, useState } from "react";

function Tick() {
  const [time, setTime] = useState(Date.toLocaleTimeString());

  useEffect(() => {
    const interval = setInterval(
      () => setTime(Date.toLocaleTimeString()),
      1000
    );
    return () => {
      clearInterval(interval);
    };
  }, []);
  return (
    <div>
      <h1>Hello, world!</h1>
      <h2>It is {time}.</h2>
    </div>
  );
}

export default Tick;
